import React, { Component } from 'react'
import { ListView, Flex, WingBlank } from 'antd-mobile';
import IMG from '../component/IMG'
const data = [
    {
        img: 't0110974b6f051c1e5a.gif',
        title: '用于构建用户界面的 JavaScript 库',
        des: 'React 使创建交互式 UI 变得轻而易举',
    },
    {
        img: 't0177ec8627c04e438b.gif',
        title: '小米新品发布会定档 MIX新品亮相',
        des: 'Yeelight智能LED灯丝灯发布：复古设计 亮度可调',
    },
    {
        img: 't0135a6567622271196.gif',
        title: 'iPhone11开始出货 本周五发售',
        des: '首批售卖的iPhone 11、iPhone 11 Pro和iPhone 11 Pro Max已经从工厂送出',
    },
];

const NUM_ROWS = 10;
let pageIndex = 11;

function genData(pIndex = 0) {
    const dataBlob = {};
    for (let i = 0; i < NUM_ROWS; i++) {
        const ii = (pIndex * NUM_ROWS) + i;
        dataBlob[`${ii}`] = `row - ${ii}`;
    }
    return dataBlob;
}

export default class LongList extends Component {
    constructor(props) {
        super(props);

        const dataSource = new ListView.DataSource({

            rowHasChanged: (row1, row2) => row1 !== row2,

        });

        this.state = {
            dataSource,
            isLoading: true,
        };
    }

    componentDidMount() {
        // 模拟ajax
        setTimeout(() => {
            this.rData = genData();
            this.setState({
                dataSource: this.state.dataSource.cloneWithRows(this.rData),
                isLoading: false,
            });
        }, 600);
    }
    // 触发滚动时间再重新渲染
    onEndReached = (event) => {
        // load new data
        // hasMore: from backend data, indicates whether it is the last page, here is false
        if (this.state.isLoading && !this.state.hasMore) {
            return;
        }
        this.setState({ isLoading: true });
        setTimeout(() => {
            this.rData = { ...this.rData, ...genData(++pageIndex) };
            this.setState({
                dataSource: this.state.dataSource.cloneWithRows(this.rData),
                isLoading: false,
            });
        }, 1000);
    }


    render() {
        let index = data.length - 1;
        const row = (rowData, sectionID, rowID) => {
            if (index < 0) {
                index = data.length - 1;
            }
            const obj = data[index--];
            return (
                <div className="flex-item">
                    <WingBlank >
                        <WingBlank size="sm"><hr /></WingBlank>
                        <Flex>

                            <Flex.Item align="start">
                                <div className="img-list"><IMG src={obj.img} /></div>

                            </Flex.Item>
                            <Flex.Item>
                                <h4>{obj.title}</h4>
                                <div style={{ lineHeight: "20px" }}>{obj.des}</div>
                            </Flex.Item>
                        </Flex>
                    </WingBlank>
                </div>
            )
        }

        return (
            <ListView

                dataSource={this.state.dataSource}
                className="am-list sticky-list"
                useBodyScroll

                renderRow={row}

                renderFooter={() => (<div style={{ padding: 30, textAlign: 'center' }}>
                    {this.state.isLoading ? '耐心一点...' : '我也是有底线的'}
                </div>)}

                // scrollEventThrottle={200}
                onEndReached={this.onEndReached}
            // onEndReachedThreshold={10}
            />
        )
    }
}
