import React, { Component } from 'react'
import { ListView } from 'antd-mobile';
const data = [
    {
        img: 't0110974b6f051c1e5a.gif',
        title: '用于构建用户界面的 JavaScript 库',
        des: 'React 使创建交互式 UI 变得轻而易举',
    },
    {
        img: 't0177ec8627c04e438b.gif',
        title: '小米新品发布会定档 MIX新品亮相',
        des: 'Yeelight智能LED灯丝灯发布：复古设计 亮度可调',
    },
    {
        img: 't0135a6567622271196.gif',
        title: 'iPhone11开始出货 本周五发售',
        des: '首批售卖的iPhone 11、iPhone 11 Pro和iPhone 11 Pro Max已经从工厂送出',
    },
];
export default class LongList extends Component {
    constructor(props) {
        super(props);

        const dataSource = new ListView.DataSource({

            rowHasChanged: (row1, row2) => row1 !== row2,

        });

        this.state = {
            dataSource,
            isLoading: true,
        };
    }

    componentDidMount() {
        // 模拟ajax
        setTimeout(() => {
            // genData();
            this.setState({
                dataSource: this.state.dataSource.cloneWithRows(data),
                isLoading: false,
            });
        }, 600);
    }
    row(obj) {
        return (
            <div className="flex-item">
                <WingBlank >
                    <WingBlank size="sm"><hr /></WingBlank>
                    <Flex>

                        <Flex.Item align="start">
                            <div className="img-list"><IMG src={obj.img} /></div>

                        </Flex.Item>
                        <Flex.Item>
                            <h4>{obj.title}</h4>
                            <div style={{ lineHeight: "20px" }}>{obj.des}</div>
                        </Flex.Item>
                    </Flex>
                </WingBlank>
            </div>
        )
    }

    render() {
        return (
            <ListView

                dataSource={this.state.dataSource}
                className="am-list sticky-list"
                useBodyScroll

                renderRow={this.row}


            // scrollEventThrottle={200}
            // onEndReached={this.onEndReached}
            // onEndReachedThreshold={10}
            />
        )
    }
}
